export function addStyleBody (styleValue) {
    document.body.classList.add(styleValue);
}

export function changeStyleBody (styleValue, styleValue2) {
    document.body.classList.add(styleValue);
    window.addEventListener('scroll',()=>{
        let windowScroll=window.pageYOffset;
        let footerPositionY=document.body.querySelector('footer').offsetTop;
        if((windowScroll)>=footerPositionY){document.body.classList.add(styleValue);}
        else{document.body.classList.remove(styleValue);}
    });
}
