export function addStyleBody (styleValue) {
    document.body.classList.add(styleValue);
}

export function changeStyleBody (styleValue) {
    window.addEventListener('scroll',()=>{
        let windowHeight=document.body.scrollTop;
        let footerHeight=document.body.querySelector('footer').offsetTop;

        document.body.classList.add(styleValue);
        document.body.classList.remove(styleValue);
    });
}
